import{_ as a}from"./chunks/desktop.DXvsyRPb.js";import{_ as e,c as o,o as t,a5 as i}from"./chunks/framework.B9ajF9kL.js";const f=JSON.parse('{"title":"Lingmo OS - 灵墨操作系统","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"zh/debian/about.md","filePath":"zh/debian/about.md"}'),r={name:"zh/debian/about.md"},n=i('<h1 id="lingmo-os-灵墨操作系统" tabindex="-1">Lingmo OS - 灵墨操作系统 <a class="header-anchor" href="#lingmo-os-灵墨操作系统" aria-label="Permalink to &quot;Lingmo OS - 灵墨操作系统&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">访问官网</p><p><a href="https://lingmo.org/" target="_blank" rel="noreferrer">LingmoOS 官网</a></p></div><h3 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h3><p><img src="'+a+'" alt="LingmoOS"> Lingmo GNU/Linux（Lingmo OS）是一个以桌面环境为主的Linux操作系统，它基于开源 Linux 内核开发，适配 <code>x86_64</code> 架构。它拥有美观的桌面环境、包含适配该桌面环境的软件、统一的 UI 风格。</p><p>Lingmo GNU/Linux 的目标 是让用户在低性能硬件上拥有华丽且实用的桌面环境以及流畅的体验。</p><h3 id="轻量化-流畅体验" tabindex="-1">轻量化，流畅体验 <a class="header-anchor" href="#轻量化-流畅体验" aria-label="Permalink to &quot;轻量化，流畅体验&quot;">​</a></h3><p>Lingmo OS 可以在一些低性能硬件上运行，相比传统的桌面操作系统，Lingmo OS 更加轻量、流畅。</p><h3 id="ui、全新的桌面环境" tabindex="-1">UI、全新的桌面环境 <a class="header-anchor" href="#ui、全新的桌面环境" aria-label="Permalink to &quot;UI、全新的桌面环境&quot;">​</a></h3><p>Lingmo OS 采取了全新的不同于传统 KDE 的设计，让操作不再繁琐；我们也对 CutefishDE 做了一些优化和重构，使操作更加符合用户，让交互与视觉更加有活力</p><h3 id="内置星火软件商店" tabindex="-1">内置星火软件商店 <a class="header-anchor" href="#内置星火软件商店" aria-label="Permalink to &quot;内置星火软件商店&quot;">​</a></h3><p>我们为了改善 Linux 用户的体验，内置了<a href="https://gitee.com/deepin-community-store/spark-store/" target="_blank" rel="noreferrer">星火软件商店</a>，用户可以在商店内下载所需的软件，使安装软件不用每次都使用命令行。当然，您也可以使用 <code>apt</code> 安装软件。</p>',11),s=[n];function l(c,h,d,m,p,_){return t(),o("div",null,s)}const g=e(r,[["render",l]]);export{f as __pageData,g as default};
